select ' Start time of AWARD_FUNDING_PROPOSALS  is '|| localtimestamp from dual/INSERT INTO AWARD_FUNDING_PROPOSALS(AWARD_FUNDING_PROPOSAL_ID,AWARD_ID,PROPOSAL_ID,UPDATE_TIMESTAMP,UPDATE_USER,VER_NBR,ACTIVE,OBJ_ID)select SEQUENCE_AWARD_ID.NEXTVAL,t.award_id, p.proposal_id,af.UPDATE_TIMESTAMP,af.UPDATE_USER,1,'Y',SYS_GUID()from  award t,proposal p,OSP$AWARD_FUNDING_PROPOSALS af,TEMP_TAB_TO_SYNC_AWARD tswhere t.award_number = replace(af.MIT_AWARD_NUMBER,'-','-00')and   t.SEQUENCE_NUMBER = af.SEQUENCE_NUMBERand  af.MIT_AWARD_NUMBER=ts.MIT_AWARD_NUMBERand  af.SEQUENCE_NUMBER=ts.SEQUENCE_NUMBERand p.proposal_number = af.proposal_numberand ts.FEED_TYPE = 'N'and p.sequence_number = af.PROP_SEQUENCE_NUMBER/commit/declareli_seq number(4,0);li_proposal_id number(12,0);cursor c_fund isselect ap.AWARD_ID,ap.PROPOSAL_ID,p.PROPOSAL_NUMBER,p.SEQUENCE_NUMBER,ap.UPDATE_TIMESTAMP,ap.UPDATE_USER from AWARD_FUNDING_PROPOSALS apinner join PROPOSAL p on ap.PROPOSAL_ID=p.PROPOSAL_IDinner join OSP$AWARD_FUNDING_PROPOSALS@coeus.kuali af on p.PROPOSAL_NUMBER=af.PROPOSAL_NUMBER and p.SEQUENCE_NUMBER=af.PROP_SEQUENCE_NUMBERinner join TEMP_TAB_TO_SYNC_AWARD t on t.MIT_AWARD_NUMBER=af.MIT_AWARD_NUMBER and t.SEQUENCE_NUMBER=af.SEQUENCE_NUMBERwhere FEED_TYPE='N'ORDER BY p.PROPOSAL_NUMBER,p.SEQUENCE_NUMBER;r_fund c_fund%rowtype;beginif c_fund%isopen thenclose c_fund;end if;open c_fund;loopfetch c_fund into r_fund;exit when c_fund%notfound;if r_fund.SEQUENCE_NUMBER<> 1 thenli_seq:=r_fund.SEQUENCE_NUMBER-1;SELECT PROPOSAL_ID INTO li_proposal_id FROM  PROPOSAL WHERE PROPOSAL_NUMBER=r_fund.PROPOSAL_NUMBER AND SEQUENCE_NUMBER=li_seq;INSERT INTO AWARD_FUNDING_PROPOSALS(AWARD_FUNDING_PROPOSAL_ID,AWARD_ID,PROPOSAL_ID,UPDATE_TIMESTAMP,UPDATE_USER,VER_NBR,ACTIVE,OBJ_ID)SELECT SEQUENCE_AWARD_ID.NEXTVAL,af.AWARD_ID,r_fund.PROPOSAL_ID,r_fund.UPDATE_TIMESTAMP,r_fund.UPDATE_USER,1,'Y',sys_guid() FROM AWARD_FUNDING_PROPOSALS af WHERE  PROPOSAL_ID=li_proposal_id;end if;end loop;close c_fund;end;/select ' Start time of AWARD_FUNDING_PROPOSALS  is '|| localtimestamp from dual;/